prompt --application/shared_components/logic/application_processes/app_id_dupl
begin
wwv_flow_imp_shared.create_flow_process(
 p_id=>wwv_flow_imp.id(54585829835997932138)
,p_process_sequence=>1
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'app_id_dupl'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'',
'l_count   number        := 0;',
'l_id      varchar2(50);',
'l_app_no varchar2(50)  := '''';',
'l_app_type  varchar2(400)  := '''';',
'l_proc_name    varchar2(500)  := '''';',
'l_fun_own    varchar2(500)  := '''';',
'l_app_dev_team    varchar2(500)  := '''';',
'l_workspace_name    varchar2(500)  := '''';',
'l_trans   varchar2(100) := '''';',
'l_error   varchar2(100) := ''N'';',
'l_messg   clob; --VARCHAR2(4000) := '''';',
'',
'begin',
'',
'    l_id          := APEX_APPLICATION.g_x01;',
'    l_app_no    := APEX_APPLICATION.g_x02;',
'    l_app_type         := APEX_APPLICATION.g_x03;',
'    l_proc_name         := APEX_APPLICATION.g_x04;',
'    l_trans       := APEX_APPLICATION.g_x05; ',
'    l_fun_own      := APEX_APPLICATION.g_x06;',
'    l_app_dev_team := APEX_APPLICATION.g_x07;',
'    l_workspace_name := APEX_APPLICATION.g_x08;',
'    ',
'    if l_app_no is null then',
'        l_messg := ''App No must have some value.'' || '';'';',
'  ',
'    end if;',
'',
'    if l_app_type is null then      ',
'      l_messg := NVL(l_messg, '''') || ''App Type must have some value.'' || '';''; ',
'    end if;',
'',
'    if l_proc_name is null then',
'      l_messg := NVL(l_messg, '''') || ''Proc name must have some value.'' || '';''; ',
'    end if;',
'',
'    if l_fun_own is null then',
'      l_messg := NVL(l_messg, '''') || ''functional owner name must have some value.'' || '';''; ',
'    end if;',
'',
'    if l_app_dev_team is null then',
'      l_messg := NVL(l_messg, '''') || ''App Dev Team  must have some value.'' || '';''; ',
'    end if;',
'',
'   ',
'    if l_workspace_name is null then',
'      l_messg := NVL(l_messg, '''') || ''Workspace name  must have some value.'' || '';''; ',
'    end if;',
'',
'    if (l_trans = ''I'') then ',
'         select count(*)',
'          into l_count',
'          from app_info',
'         where NVL(app_no, '' '') = NVL(l_app_no, '' '') ',
'               and 	upper(APPLICATION_TYPE) = upper(l_app_type)	 -- old DCN = l_dcn',
'               and upper(PROCESS_NAME) = upper(l_proc_name)',
'		   ;  ',
'',
'        if (l_count > 0) then	',
'	        l_messg := NVL(l_messg, '''') || ''App no already exists in  table  '' || l_app_no || ''.'' || '';'';     ',
'        end if;	',
'    end If;',
'',
'    if (l_trans = ''U'') then    ',
'        select count(*)',
'          into l_count',
'          from app_info',
'         where NVL(app_no, '' '') = NVL(l_app_no, '' '') ',
'               and 	upper(APPLICATION_TYPE) = upper(l_app_type)	 -- old DCN = l_dcn',
'               and upper(PROCESS_NAME) = upper(l_proc_name);',
'             ',
'        if l_count = 0 then               ',
'          select count(*)',
'          into l_count',
'          from app_info',
'         where NVL(app_no, '' '') = NVL(l_app_no, '' '') ',
'               and 	upper(APPLICATION_TYPE) = upper(l_app_type)	 -- old DCN = l_dcn',
'               and upper(PROCESS_NAME) = upper(l_proc_name); ',
'',
'            if (l_count > 0) then',
'                l_count := 2;',
'            end if;                              ',
'         end if;',
'',
'         if (l_count > 1) then',
'	        l_messg := NVL(l_messg, '''') || ''Entry already exists in  table '' ||l_app_no|| ''.'' || '';'';',
'         end If;',
'',
'     end if;',
'',
'     if l_messg is null then',
'        l_messg := ''NOERROR'';',
'     end if;',
'',
'     htp.p(l_messg);',
'  ',
'end;'))
